const output = JSON.parse($input.all()[0].json.stdout);
const costs = output.ResultsByTime[0].Groups;
const formatted = costs.map(c => \`\${c.Keys[0]}: \$\${parseFloat(c.Metrics.AmortizedCost.Amount).toFixed(2)}\`).join('\n');

return {
  json: {
    result: \`Custos Klm (último mês):\n\${formatted}\`
  }
};
